export let emoteNameToUrl = {};
const assetLoc =
  process.env.NODE_ENV === 'development'
    ? 'assets/emotes'
    : 'resources/assets/emotes';
function scan_for_files(dir: string, return_prefix: string) {
  const files = [];
  for (const file of fs.readdirSync(dir)) {
    const stats = fs.statSync(dir + '/' + file);
    if (stats.isDirectory()) {
      for (const f of scan_for_files(dir + '/' + file, file+'/')) {
        files.push(f);
      }
    } else {
      files.push(file);
    }
  }
  return files.map(f => return_prefix + f);
}
for (const file of scan_for_files(assetLoc, assetLoc + '/')) {
  const emoteName = file.substr(file.lastIndexOf('/')+1).split('.')[0]
  emoteNameToUrl[emoteName] = '../' + file;
}
